mapper.update:
UPDATE t_dic
SET zh_name = #{zhName},
en_name = #{enName},
business_attribute = #{businessAttribute}
WHERE id = #{id}

mapper.select:
SELECT id, zh_name AS zhName, en_name AS enName, business_attribute AS businessAttribute
FROM t_dic
WHERE id = #{id}

SELECT id, zh_name AS zhName, en_name AS enName, business_attribute AS businessAttribute
FROM t_dic
WHERE en_name = #{enName}

SELECT * FROM t_dic
AND (zh_name LIKE CONCAT('%', #{keyword}, '%') OR en_name LIKE CONCAT('%', #{keyword}, '%'))
LIMIT #{pageSize} OFFSET #{pageNum}
