mapper.select:
SELECT id, dic_id AS dicId, zh_name AS zhName, en_name AS enName, field_type AS fieldType, es_mapping_type AS esMappingType
FROM t_field
WHERE id = #{id}

SELECT id, dic_id AS dicId, zh_name AS zhName, en_name AS enName, field_type AS fieldType, es_mapping_type AS esMappingType
FROM t_field
WHERE dic_id = #{dicId}

SELECT id, dic_id AS dicId, zh_name AS zhName, en_name AS enName, field_type AS fieldType, es_mapping_type AS esMappingType
FROM t_field
WHERE en_name = #{enName}

