mapper.update:
UPDATE t_life_policy
SET zh_name = #{entity.zhName},
en_name = #{entity.enName},
policy_value = #{entity.policyValue}
WHERE id = #{entity.id}

mapper.select:
SELECT * FROM t_life_policy
zh_name LIKE CONCAT('%', #{keyword}, '%') OR en_name LIKE CONCAT('%', #{keyword}, '%')
ORDER BY update_time DESC LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM t_life_policy
WHERE en_name = #{enName}

    AND id != #{excludeId}

ADN cluster_code = #{clusterCode}
